// src/components/HealthcareInventoryForm.js
import React, { useState } from 'react';
import axios from 'axios';

function HealthcareInventoryForm() {
  const [formData, setFormData] = useState({
    itemName: '',
    quantity: '',
    location: '',
    photo: null,
    geoLocation: { lat: null, lng: null },
  });

  const [qrCode, setQrCode] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handlePhotoCapture = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true, // Use the default camera (laptop webcam)
      });

      const video = document.createElement('video');
      video.srcObject = stream;

      await new Promise((resolve) => (video.onloadedmetadata = resolve));

      const canvas = document.createElement('canvas');
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      const context = canvas.getContext('2d');

      video.play();
      context.drawImage(video, 0, 0, canvas.width, canvas.height);

      stream.getTracks().forEach((track) => track.stop());

      const photoDataUrl = canvas.toDataURL('image/png');

      // Obtain geolocation data
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            const geoLocation = {
              lat: position.coords.latitude,
              lng: position.coords.longitude,
            };

            setFormData({
              ...formData,
              photo: photoDataUrl,
              geoLocation,
            });
          },
          (error) => {
            console.error('Error obtaining geolocation:', error);
          }
        );
      }
    } catch (error) {
      console.error('Error capturing photo:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Send form data to the backend for QR code generation
    try {
      const response = await axios.post('http://localhost:5000/api/generate-qr', formData);

      setQrCode(response.data.qrCode);
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  return (
    <div className="max-w-md mx-auto p-4 border border-gray-300 rounded-lg shadow-md">
      <h2 className="text-2xl font-semibold mb-4">Healthcare Inventory Form</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700 mb-2" htmlFor="itemName">
            Item Name
          </label>
          <input
            type="text"
            id="itemName"
            name="itemName"
            value={formData.itemName}
            onChange={handleInputChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            placeholder="Enter item name"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2" htmlFor="quantity">
            Quantity
          </label>
          <input
            type="number"
            id="quantity"
            name="quantity"
            value={formData.quantity}
            onChange={handleInputChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            placeholder="Enter quantity"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2" htmlFor="location">
            Location
          </label>
          <input
            type="text"
            id="location"
            name="location"
            value={formData.location}
            onChange={handleInputChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            placeholder="Enter location"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Capture Photo</label>
          <button
            type="button"
            onClick={handlePhotoCapture}
            className="px-4 py-2 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600"
          >
            Capture Photo
          </button>
          {formData.geoLocation.lat && formData.geoLocation.lng && (
            <p className="mt-2 text-gray-600">
              Latitude: {formData.geoLocation.lat}, Longitude: {formData.geoLocation.lng}
            </p>
          )}
        </div>

        <button
          type="submit"
          className="w-full px-4 py-2 bg-green-500 text-white font-semibold rounded-lg hover:bg-green-600"
        >
          Submit
        </button>
      </form>

      {qrCode && (
        <div className="mt-4">
          <h3 className="text-lg font-semibold mb-2">QR Code:</h3>
          <img src={qrCode} alt="QR Code" />
        </div>
      )}
    </div>
  );
}

export default HealthcareInventoryForm;
