import React, { useState, useEffect } from 'react';

function InventoryList() {
  const [items, setItems] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('http://localhost:5000/api/healthcare'); // Adjust the URL as needed
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setItems(data);
      } catch (error) {
        console.error('Error fetching data:', error);
        setError(error.message);
      }
    }

    fetchData();
  }, []);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h1 className="text-3xl font-bold mb-8">Inventory List</h1>
      {error && <p className="text-red-500 mb-4">Error: {error}</p>}
      <div className="w-full max-w-4xl grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {items.length > 0 ? (
          items.map((item, index) => (
            <div key={index} className="bg-white border border-gray-300 rounded-lg shadow-lg p-4">
              <img
                src={item.camera_photo}
                alt={item.item_name}
                className="w-full h-48 object-cover rounded-md mb-4"
              />
              <h2 className="text-xl font-semibold mb-2">{item.item_name}</h2>
              <p className="text-gray-600 mb-1">
                <span className="font-medium">Quantity:</span> {item.quantity}
              </p>
              <p className="text-gray-600 mb-1">
                <span className="font-medium">Location:</span> {item.location}
              </p>
              <p className="text-gray-600 mb-1">
                <span className="font-medium">Expiry Date:</span> {new Date(item.expiry_date).toLocaleDateString()}
              </p>
              <p className="text-gray-600 mb-1">
                <span className="font-medium">Manufacturing Date:</span> {new Date(item.manufacturing_date).toLocaleDateString()}
              </p>
              <p className="text-gray-600 mb-1">
                <span className="font-medium">Manufacturer:</span> {item.manufacturer_name}
              </p>
            </div>
          ))
        ) : (
          <p className="text-gray-500 text-center col-span-full">No items found.</p>
        )}
      </div>
    </div>
  );
}

export default InventoryList;
