import React, { useState, useRef } from 'react';

const NoteInput = () => {
  const [isFocused, setIsFocused] = useState(false);
  const containerRef = useRef(null);

  const handleFocus = () => {
    setIsFocused(true);
  };

  const handleBlur = (event) => {
    if (containerRef.current && !containerRef.current.contains(event.relatedTarget)) {
      setIsFocused(false);
    }
  };

  return (
    <div 
      ref={containerRef}
      className={`border w-[400px] h-[100px] p rounded-lg shadow-sm p-4 ${isFocused ? 'bg-white' : 'bg-gray-100'}`}
      onClick={handleFocus}
      onBlur={handleBlur}
      tabIndex={-1}
      style={{ outline: 'none' }}
    >
      {isFocused && (
        <input 
          type="text" 
          placeholder="Title" 
          className="w-full bg-transparent border-none text-lg mb-2"
          autoFocus
        />
      )}
      <textarea 
        placeholder="Take a note..." 
        className="w-full bg-transparent border-none resize-none"
        rows={isFocused ? 3 : 1}
      />
      {isFocused && (
        <div className="flex justify-between mt-2">
          <div className="flex space-x-2">
            <button className="text-gray-500">
              <i className="fas fa-bell"></i>
            </button>
            <button className="text-gray-500">
              <i className="fas fa-user-plus"></i>
            </button>
            <button className="text-gray-500">
              <i className="fas fa-palette"></i>
            </button>
            <button className="text-gray-500">
              <i className="fas fa-image"></i>
            </button>
            <button className="text-gray-500">
              <i className="fas fa-archive"></i>
            </button>
            <button className="text-gray-500">
              <i className="fas fa-ellipsis-v"></i>
            </button>
          </div>
          <button 
            className="text-gray-500" 
            onMouseDown={(e) => {
              e.preventDefault();
              setIsFocused(false);
            }}
          >
            Close
          </button>
        </div>
      )}
    </div>
  );
};

export default NoteInput;
