// components/Department/DepartmentHome.js
import React, { useState, useEffect } from 'react';

const DepartmentHome = () => {
  const [healthcareItems, setHealthcareItems] = useState([]);

  useEffect(() => {
    // Fetch data from the server
    const fetchHealthcareItems = async () => {
      try {
        const response = await fetch('/api/healthcare');
        const data = await response.json();
        setHealthcareItems(data);
      } catch (err) {
        console.error('Error fetching healthcare items:', err);
      }
    };

    fetchHealthcareItems();
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Healthcare Items</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {healthcareItems.map((item, index) => (
          <div key={index} className="bg-white shadow-md rounded-lg overflow-hidden">
            <img src={item.img} alt={item.title} className="w-full h-48 object-cover" />
            <div className="p-4">
              <h2 className="text-lg font-semibold">{item.title}</h2>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DepartmentHome;
