import React, { useState, useRef } from "react";
import axios from "axios";
import QRCode from "qrcode.react"; // Import the QRCode component

function HealthcareInventoryForm() {
  const [formData, setFormData] = useState({
    itemName: "",
    quantity: "",
    location: "",
    expiryDate: "",
    manufacturingDate: "",
    manufacturerName: "",
    frontCameraPhoto: null,
    backCameraPhoto: null,
  });

  const [qrCode, setQrCode] = useState("");
  const [geoLocation, setGeoLocation] = useState({ lat: null, lng: null });
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const startCamera = async (cameraType) => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: cameraType },
      });
      const video = videoRef.current;
      video.srcObject = stream;
      video.play();
    } catch (error) {
      console.error("Error accessing camera: ", error);
    }
  };

  const stopCamera = () => {
    const video = videoRef.current;
    const stream = video.srcObject;
    if (stream) {
      const tracks = stream.getTracks();
      tracks.forEach((track) => track.stop());
      video.srcObject = null;
    }
  };

  const capturePhoto = async (cameraType) => {
    const canvas = canvasRef.current;
    const video = videoRef.current;

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    const context = canvas.getContext("2d");
    context.drawImage(video, 0, 0, canvas.width, canvas.height);
    stopCamera();

    const photoDataUrl = canvas.toDataURL("image/png");
    setFormData({
      ...formData,
      [cameraType === "user" ? "frontCameraPhoto" : "backCameraPhoto"]:
        photoDataUrl,
    });

    // Update geolocation when photo is captured
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setGeoLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          });
        },
        (error) => {
          console.error("Error obtaining geolocation:", error);
        }
      );
    }
  };

  const handleGeoLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setGeoLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          });
        },
        (error) => {
          console.error("Error obtaining geolocation:", error);
        }
      );
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare the data for QR code generation
    const dataToEncode = JSON.stringify({
      ...formData,
      geoLocation,
    });

    // Generate QR code URL
    setQrCode(dataToEncode);

    // Optionally, send the data to the backend or perform other actions
    try {
      const response = await axios.post("http://localhost:5000/api/generate-qr", {
        ...formData,
        geoLocation,
      });

      setQrCode(response.data.qrCode);
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  return (
    <div className="max-w-md mx-auto p-4 border border-gray-300 rounded-lg shadow-md">
      <h2 className="text-2xl font-semibold mb-4">Healthcare Inventory Form</h2>
      <form onSubmit={handleSubmit}>
        {/* Form fields */}
        <div className="mb-4">
          <label className="block text-gray-700 mb-2" htmlFor="itemName">
            Item Name
          </label>
          <input
            type="text"
            id="itemName"
            name="itemName"
            value={formData.itemName}
            onChange={handleInputChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            placeholder="Enter item name"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2" htmlFor="quantity">
            Quantity
          </label>
          <input
            type="number"
            id="quantity"
            name="quantity"
            value={formData.quantity}
            onChange={handleInputChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            placeholder="Enter quantity"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2" htmlFor="manufacturerName">
            Name of Manufacturer
          </label>
          <input
            type="text"
            id="manufacturerName"
            name="manufacturerName"
            value={formData.manufacturerName}
            onChange={handleInputChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            placeholder="Enter name of manufacturer"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2" htmlFor="expiryDate">
            Expiry Date
          </label>
          <input
            type="date"
            id="expiryDate"
            name="expiryDate"
            value={formData.expiryDate}
            onChange={handleInputChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2" htmlFor="manufacturingDate">
            Manufacturing Date
          </label>
          <input
            type="date"
            id="manufacturingDate"
            name="manufacturingDate"
            value={formData.manufacturingDate}
            onChange={handleInputChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2" htmlFor="location">
            Location
          </label>
          <input
            type="text"
            id="location"
            name="location"
            value={formData.location}
            onChange={handleInputChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            placeholder="Enter location"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Capture Photos</label>
          <button
            type="button"
            onClick={() => startCamera("user")}
            className="px-4 py-2 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600 mr-2"
          >
            Capture Front Camera
          </button>
          <button
            type="button"
            onClick={() => startCamera("environment")}
            className="px-4 py-2 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600"
          >
            Capture Back Camera
          </button>
          <button
            type="button"
            onClick={() => capturePhoto("user")}
            className="px-4 py-2 bg-green-500 text-white font-semibold rounded-lg hover:bg-green-600 mt-2"
          >
            Capture Photo
          </button>
        </div>

        <div className="mb-4">
          <video ref={videoRef} width="640" height="480" autoPlay />
          <canvas ref={canvasRef} style={{ display: "none" }}></canvas>
          {formData.frontCameraPhoto && (
            <img
              src={formData.frontCameraPhoto}
              alt="Front Camera"
              className="mt-2"
            />
          )}
          {formData.backCameraPhoto && (
            <img
              src={formData.backCameraPhoto}
              alt="Back Camera"
              className="mt-2"
            />
          )}
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Geolocation</label>
          <button
            type="button"
            onClick={handleGeoLocation}
            className="px-4 py-2 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600"
          >
            Get Current Location
          </button>
          {geoLocation.lat && geoLocation.lng && (
            <p className="mt-2 text-gray-600">
              Latitude: {geoLocation.lat}, Longitude: {geoLocation.lng}
            </p>
          )}
        </div>

        <button
          type="submit"
          className="w-full px-4 py-2 bg-green-500 text-white font-semibold rounded-lg hover:bg-green-600"
        >
          Submit
        </button>
      </form>

      {qrCode && (
        <div className="mt-4">
          <h3 className="text-lg font-semibold mb-2">QR Code:</h3>
          <QRCode value={qrCode} size={256} />
        </div>
      )}
    </div>
  );
}

export default HealthcareInventoryForm;
