import React, { useState, useEffect } from 'react';

function DepartmentHome() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('/api/healthcare');
        const data = await response.json();
        setItems(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    fetchData();
  }, []);

  return (
    <div>
      <h1>Department Home</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {items.map(item => (
          <div key={item.item_name} className="border p-4 rounded-lg shadow-md">
            <img src={item.camera_photo} alt={item.item_name} className="w-full h-auto rounded-md mb-2" />
            <h2 className="text-xl font-bold">{item.item_name}</h2>
          </div>
        ))}
      </div>
    </div>
  );
}

export default DepartmentHome;
