import React from 'react';

const WarehouseSpace = () => {
  // Static data
  const warehouseData = {
  totalSpace: 1000,
  occupiedSpace: 600,
  remainingSpace: 400,
  products: [
    { item_name: "Product A", space_occupied: 150 },
    { item_name: "Product B", space_occupied: 200 },
    { item_name: "Product C", space_occupied: 250 }
  ]
};

  const data = warehouseData;

  const { totalSpace, occupiedSpace, remainingSpace, products } = data;

  // Calculate percentages for pie chart
  const occupiedPercentage = (occupiedSpace / totalSpace) * 100;
  const remainingPercentage = (remainingSpace / totalSpace) * 100;

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Warehouse Space Overview</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {/* Pie Chart for Space Usage */}
        <div className="bg-white p-4 shadow rounded">
          <h3 className="text-xl font-semibold mb-2">Space Usage</h3>
          <div className="flex justify-center items-center">
            <div className="relative w-64 h-64">
              <svg viewBox="0 0 32 32" className="w-full h-full">
                <circle
                  cx="16"
                  cy="16"
                  r="15.91549431"
                  stroke="#FF6384"
                  strokeWidth="7"
                  strokeDasharray={`${occupiedPercentage} ${100 - occupiedPercentage}`}
                  strokeLinecap="round"
                  fill="none"
                />
                <circle
                  cx="16"
                  cy="16"
                  r="15.91549431"
                  stroke="#36A2EB"
                  strokeWidth="7"
                  strokeDasharray={`${remainingPercentage} ${100 - remainingPercentage}`}
                  strokeLinecap="round"
                  fill="none"
                  strokeDashoffset={`${100 - occupiedPercentage}`}
                />
                <text
                  x="50%"
                  y="50%"
                  textAnchor="middle"
                  dy=".3em"
                  fontSize="1.5em"
                  fill="#333"
                >
                  {remainingSpace} / {totalSpace}
                </text>
              </svg>
            </div>
          </div>
        </div>

        {/* Bar Chart for Product Space Usage */}
        <div className="bg-white p-4 shadow rounded">
          <h3 className="text-xl font-semibold mb-2">Products and Space Usage</h3>
          <div className="w-full">
            {products.map(product => (
              <div key={product.item_name} className="flex items-center mb-2">
                <div className="w-1/4 font-semibold">{product.item_name}</div>
                <div className="w-3/4 bg-gray-200 rounded h-4 relative">
                  <div
                    className="absolute top-0 left-0 bg-blue-500 h-full rounded"
                    style={{ width: `${(product.space_occupied / totalSpace) * 100}%` }}
                  />
                  <div className="absolute inset-0 flex items-center justify-center text-white">
                    {product.space_occupied} units
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default WarehouseSpace;
