import React, { useState, useRef } from "react";
import axios from "axios";
import { Link } from 'react-router-dom';

function AddProductToDept() {
  const [formData, setFormData] = useState({
    itemName: "",
    quantity: "",
    location: "",
    expiryDate: "",
    manufacturingDate: "",
    manufacturerName: "",
    cameraPhoto: null, // Only one photo now
  });

  const [geoLocation, setGeoLocation] = useState({ lat: null, lng: null });
  const [submissionStatus, setSubmissionStatus] = useState(null); // To track form submission status
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const startCamera = async (cameraType) => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: cameraType },
      });
      const video = videoRef.current;
      video.srcObject = stream;
      video.play();
    } catch (error) {
      console.error("Error accessing camera: ", error);
    }
  };

  const stopCamera = () => {
    const video = videoRef.current;
    const stream = video.srcObject;
    if (stream) {
      const tracks = stream.getTracks();
      tracks.forEach((track) => track.stop());
      video.srcObject = null;
    }
  };

  const capturePhoto = async () => {
    const canvas = canvasRef.current;
    const video = videoRef.current;

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    const context = canvas.getContext("2d");
    context.drawImage(video, 0, 0, canvas.width, canvas.height);
    stopCamera();

    const photoDataUrl = canvas.toDataURL("image/png");
    setFormData({
      ...formData,
      cameraPhoto: photoDataUrl, // Only one photo
    });

    // Update geolocation when photo is captured
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setGeoLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          });
        },
        (error) => {
          console.error("Error obtaining geolocation:", error);
        }
      );
    }
  };

  const handleGeoLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setGeoLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          });
        },
        (error) => {
          console.error("Error obtaining geolocation:", error);
        }
      );
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Construct payload for submission
    const payload = {
      item_name: formData.itemName,
      quantity: parseInt(formData.quantity, 10),
      location: formData.location,
      expiry_date: formData.expiryDate,
      manufacturing_date: formData.manufacturingDate,
      manufacturer_name: formData.manufacturerName,
      camera_photo: formData.cameraPhoto,
      latitude: geoLocation.lat,
      longitude: geoLocation.lng,
    };

    try {
      const response = await axios.post("http://localhost:5000/api/add-product", payload);

      if (response.status === 201) {
        console.log("Product added successfully");
        setSubmissionStatus("success");
        // Reset form after successful submission
        setFormData({
          itemName: "",
          quantity: "",
          location: "",
          expiryDate: "",
          manufacturingDate: "",
          manufacturerName: "",
          cameraPhoto: null,
        });
        setGeoLocation({ lat: null, lng: null });
      } else {
        console.error("Error adding product:", response.data.message);
        setSubmissionStatus("error");
      }
    } catch (error) {
      console.error("Error submitting form:", error);
      setSubmissionStatus("error");
    }
  };

  return (
    <div className="max-w-md mx-auto p-4 border border-gray-300 rounded-lg shadow-md bg-white">
      <h2 className="text-2xl font-semibold mb-4">Add Product to Department</h2>
      <form onSubmit={handleSubmit}>
        {/* Form Fields */}
        <div className="mb-4">
          <label className="block text-gray-700 mb-2" htmlFor="itemName">
            Item Name
          </label>
          <input
            type="text"
            id="itemName"
            name="itemName"
            value={formData.itemName}
            onChange={handleInputChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            placeholder="Enter item name"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2" htmlFor="quantity">
            Quantity
          </label>
          <input
            type="number"
            id="quantity"
            name="quantity"
            value={formData.quantity}
            onChange={handleInputChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            placeholder="Enter quantity"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2" htmlFor="manufacturerName">
            Name of Manufacturer
          </label>
          <input
            type="text"
            id="manufacturerName"
            name="manufacturerName"
            value={formData.manufacturerName}
            onChange={handleInputChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            placeholder="Enter name of manufacturer"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2" htmlFor="expiryDate">
            Expiry Date
          </label>
          <input
            type="date"
            id="expiryDate"
            name="expiryDate"
            value={formData.expiryDate}
            onChange={handleInputChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2" htmlFor="manufacturingDate">
            Manufacturing Date
          </label>
          <input
            type="date"
            id="manufacturingDate"
            name="manufacturingDate"
            value={formData.manufacturingDate}
            onChange={handleInputChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2" htmlFor="location">
            Location
          </label>
          <input
            type="text"
            id="location"
            name="location"
            value={formData.location}
            onChange={handleInputChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            placeholder="Enter location"
          />
        </div>

        {/* Capture Photos */}
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Capture Photo</label>
          <button
            type="button"
            onClick={() => startCamera("user")}
            className="px-4 py-2 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600 mr-2"
          >
            Start Camera
          </button>
          <button
            type="button"
            onClick={capturePhoto}
            className="px-4 py-2 bg-green-500 text-white font-semibold rounded-lg hover:bg-green-600 mt-2"
          >
            Capture Photo
          </button>
        </div>

     {/* Display Photo */}
<div className="mb-4">
  <video ref={videoRef} width="640" height="480" autoPlay />
  <canvas ref={canvasRef} style={{ display: "none" }}></canvas>
  {formData.cameraPhoto && (
    <img
      src={formData.cameraPhoto}
      alt="Captured item"
      className="mt-2"
    />
  )}
</div>


        {/* Geolocation */}
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Geolocation</label>
          <button
            type="button"
            onClick={handleGeoLocation}
            className="px-4 py-2 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600"
          >
            Get Current Location
          </button>
          {geoLocation.lat && geoLocation.lng && (
            <p className="mt-2 text-gray-600">
              Latitude: {geoLocation.lat}, Longitude: {geoLocation.lng}
            </p>
          )}
        </div>

        <button
          type="submit"
          className="w-full py-3 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600"
        >
          Add Product
        </button>
      </form>

      {submissionStatus === "success" && (
        <p className="text-green-500 mt-4">Product added successfully!</p>
      )}
      {submissionStatus === "error" && (
        <p className="text-red-500 mt-4">Error adding product. Please try again.</p>
      )}
    </div>
  );
}

export default AddProductToDept;
